package Basic;

public class X_Opattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a=5;
		 int b=5;
		 for(int i=0;i<a;i++)
		 {
			 for(int j=0;j<=b;j++)
			 {
				 if(i==j)
				 {
					 System.out.print("X");
				 }
				 else
				 {
					 System.out.print("O");
				 }
			 }
		 
		 System.out.println();

	}
	}

}
