package Basic;


public class evenfrom1_20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        //print even no from 1 to 20
		//step 1:for loop with the
		//initial value of i set to
		for(int i=1;i<=20;i++)
		{
			 
			if(i%2==0)
			{
			System.out.print(i+ "is an even number");
			}
		}
	}
}